import React, { useEffect, useState } from "react";
import CV from "../../assets/CV.pdf"
import { AiOutlineLike } from "react-icons/ai"
import { Player, Controls } from '@lottiefiles/react-lottie-player';

const CTA = () => {
    const [counter, setCounter] = useState(0);

    const likeCounter = () => {
        setCounter(counter + 1);
    }

    useEffect(() => {}, [counter]);

    return(
        <div className="cta">
            <Player autoplay loop src="https://assets3.lottiefiles.com/packages/lf20_UJNc2t.json" style={{ height: '300px', width: '300px' }}>
            </Player>
            <a href={CV} download className="btn">Download CV</a>
            <a href="#contact" className="btn btn-primary">Let's Talk</a>
            <a onClick={likeCounter} className="btn btn-secondary">{counter} <AiOutlineLike className="btn-secondary-image"/></a>
        </div>
    )
}

export default CTA
