import React, { useEffect, useRef } from 'react';
import './header.css'
import CTA from './CTA'
import ME from '../../assets/me-4.png'
import HeaderSocials from "./HeaderSocials"
import { Player } from '@lottiefiles/react-lottie-player';

const Header = () => {
    useEffect(() => {
        const parallaxDomElement = document.querySelector('#parallax__1');
          document.addEventListener("mousemove", function (e) {
            let x = e.clientX;
            let y = e.clientY;

            parallaxDomElement.style.transform = `translate(${x}px, ${y}px})`;
            parallaxDomElement.style.backgroundPositionX = y * 100 + "%";
          })
    }, []);

    return (
        <header>
            <div className="container header__header">
                <Player autoplay loop src="https://assets4.lottiefiles.com/private_files/lf30_uk8fs7hs.json" style={{ height: '300px', width: '300px' }}>
                </Player>
                <div>
                    <h1 id="parallax__1">Nicolas Mercier</h1>
                    <h4>Fullstack Developer</h4>
                </div>
            </div>
            <div className="container header__container">
            <div className="me">
                    <img src={ME} alt="me" />
                </div>
                <CTA />
                <HeaderSocials />

                <a href="#contact" className="scroll__down">Scroll Down</a>

            </div>
        </header>
    )
}

export default Header
